<template>
	<text class="icon" :class="'icon--' + name + '--' + theme" :style="{width: size + 'px',height: size + 'px'}" @tap="iconTapHandler">
		<text v-if="badge" class="icon_badge">{{badgeText}}</text>
	</text>
</template>

<script>
	export default {
		props: {
			name: {
				type: String
			},
			theme: {
				type: String,
				default: 'light'
			},
			size: {
				type: String,
				default: '14'
			},
			badge: {
				type: Boolean,
				default: false
			},
			badgeText: {
				type: String,
				default: ''
			}
		},
		methods: {
			iconTapHandler() {
				uni.$emit('tap')
			}
		}
	}
</script>

<style lang="scss">
	@import "../static/scss/settings/_variable.scss";
	
	/*
	 * snow: #fff
	 * light: #959393
	 * */
	.icon {
		position: relative;
		display: inline-block;
		vertical-align: middle;
		background-position: center;
		background-size: contain;
		
		.icon_badge {
			position: absolute;
			top: -24rpx;
			right: -24rpx;
			width: 48rpx;
			line-height: 48rpx;
			font-size: 32rpx;
			color: $themeColor;
			text-align: center;
			border-radius: 50%;
			background-color: #fff;
			opacity: .85;
			transform: scale(.5);
		}
	}
	
